
public class Operadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1, num2, resultado;
		
		num1 = 7;
		num2 = 5;
		resultado = num1 + num2;
		
		System.out.println(resultado);
		
		System.out.println(num1-num2);
		System.out.println(num1*num2);
		System.out.println(num1/num2);
		
		num2+=10;
		System.err.println(num2);

	}

}
